import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { UserState } from "../context/UserProvider";
import axios from "axios";

const Score = ({ socket }) => {
  const { user, score, setScore, token } = UserState();
  const navigate = useNavigate();

  useEffect(() => {
    socket.on("updated-leaderboard", (leaderboard) => {
      let position = leaderboard.users;
      setScore(position);
    });
    getScore();
  }, []);

  const getScore = async () => {
    let config = {
      headers: {
        Authorization: "Bearer " + token,
        withCredentials: true,
      },
    };

    try {
      const res = await axios.get(`http://localhost:5000/user/room/get`, config);
      setScore(res.data.scores.users);
    } catch (err) {
      console.log("Error fetching scores:", err.message);
    }
  };

  const copyToClipboard = async (text) => {
    try {
      await navigator.clipboard.writeText(text);
      console.log("Copied to clipboard:", text);
    } catch (err) {
      console.error("Failed to copy:", err);
    }
  };

  return (
    <div className="flex flex-col items-center justify-start min-h-screen bg-yellow-300 p-5">
      <p
        className="text-4xl mt-5 cursor-pointer text-white"
        onClick={() => navigate(-1)}
      >
        <i className="fas fa-angle-left mr-3"></i>
        Leaderboard
      </p>
      <div className="flex flex-col items-center justify-between w-full mt-5 max-w-lg">
        {/* Rank List */}
        <div className="flex flex-col w-full max-w-lg bg-white rounded-lg shadow-xl overflow-y-auto">
          <ul className="w-full">
            {score.map((item, index) => (
              <li
                key={item._id}
                className={`${
                  index % 2 === 0 ? "bg-gray-100" : "bg-white"
                } w-full px-5 py-3 flex items-center justify-between border-b border-gray-200 last:border-b-0`}
              >
                <span className="font-bold text-lg">{item.position}</span>
                <span className="text-lg">{item.user?.name}</span>
              </li>
            ))}
          </ul>
        </div>

        {/* Referral Link */}
        <div className="flex flex-col w-full max-w-lg mt-5">
          {score
            .filter((item) => item.user && item.user.email === user.email)
            .map((item) => (
              <div
                key={item._id}
                className="flex flex-col items-center justify-center border-3 rounded-lg shadow-lg bg-orange-500 text-white p-5"
              >
                <p className="text-lg font-bold">
                  Your referral code:{" "}
                  <span className="font-extrabold">{item.user.referralCode}</span>
                  <button
                    className="ml-2 focus:outline-none"
                    onClick={() => copyToClipboard(item.user.referralCode)}
                  >
                    <i className="far fa-clipboard"></i>
                  </button>
                </p>
              </div>
            ))}
        </div>
      </div>

      {/* Instructions */}
      <div className="mt-5 text-white max-w-lg">
        <ul>
          <li className="text-lg font-semibold mb-2">
            Offer on the iPhone 15 for 1st position
          </li>
          <li className="text-lg font-semibold">
            Earn 1 position forward on each referral
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Score;
