import React, { useEffect } from "react";
import iphone from "../utils/iphone.png";
import backgroundImage from "../utils/background.avif"; // Ensure the file name is correct
import axios from "axios";
import { UserState } from "../context/UserProvider";
import { Link, useNavigate } from "react-router-dom";

const HomePage = () => {
  const { token, setUser, user } = UserState();

  const navigate = useNavigate();

  const getInfo = async (token) => {
    let config = {
      headers: {
        Authorization: "Bearer " + token,
        withCredentials: true,
      },
    };

    axios
      .get(`http://localhost:5000/user/get-info`, config)
      .then((response) => {
        const { user } = response.data;
        setUser(user);
      })
      .catch((err) => {
        localStorage.removeItem("signedJWT");
        console.log("invalid jwt user not authenticated");
        navigate("/");
      });
  };

  useEffect(() => {
    const timer = setTimeout(() => {
      getInfo(token);
    }, 100);
    return () => clearTimeout(timer);
  }, [token]);

  return (
    <div 
      style={{ backgroundImage: `url(${backgroundImage})`, backgroundSize: "contain",display:"flex",flexDirection:"column",justifyContent:"center",alignItems:"center",height:"100vh"}}
    >
      <div className="h-[80vh] w-[100vw] bg-yellow overflow-x-hidden relative flex flex-col"> 
        <img
          src={iphone}
          // className="object-contain w-[60%] h-[100px] sm:w-[100%] sm:h-[100%] absolute top-[15%] left-[10%] sm:left-[10%] md:left-[10%]"
          style={{height:"600px",width:"700px",border:"1px solid bla"}}
          alt="iPhone"
        />
      </div>
      <div className="mt-[-10%] w-[100%] flex-1 flex justify-center items-center">
        {user && (
          <div className="mt-[10%] md:mt-[7%] sm:mx-[10%]">
            {!user.joinedRoom && (
              <div className="text-center">
                <p className="text-2xl sm:text-2xl md:text-4xl bg-clip-text text-transparent bg-gradient-to-r from-orange-500 to-yellow-500">
                  <Link to={"/early-register"}>Register</Link>
                </p>
                <Link to={"/early-register"}>
                  <button className="mt-4 px-4 py-2 bg-orange-500 text-white rounded-lg hover:bg-orange-600">
                    Register
                  </button>
                </Link>
              </div>
            )}
            {user.joinedRoom && (
              <div className="flex justify-center items-center h-screen">
                <p className="text-2xl cursor-pointer sm:text-2xl md:text-4xl bg-clip-text text-transparent bg-gradient-to-r from-orange-500 to-yellow-500">
                  <Link to={"/leader-board"}>Leaderboard</Link>
                </p>
              </div>
            )}
            {user.winner && (
              <p className="text-2xl cursor-pointer sm:text-2xl md:text-4xl bg-clip-text text-transparent bg-gradient-to-r from-indigo-800 via-purple-700 to-pink-600">
                <Link to={"/reedem-coupons"}>Coupons</Link>
              </p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default HomePage;
