import React, { useState } from "react";
import axios from "axios";

import { toast } from "react-toastify";

import { useNavigate } from "react-router-dom";

const Register = ({ handleLogin }) => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [loding, setLoding] = useState(false);

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name || !email || !password || !confirmPassword) {
      toast.error("please fill all fields");
      return;
    }
    if (password != confirmPassword) {
      return toast.error("confirm password incorrect");
    }
    setLoding(true);
    const user = { name, email, password };
    console.log(user);  

    // const data = await axios
    //   .post(`http://localhost:5000/auth/register`, {
    //     name,email,password
    //   })
    //   console.log(data);
      // .then((response)=>response.json())
      // .then((response) => {
      //   if (response.status !== 200) {
      //     console.log(response.data.message);
      //     toast(response.data.message);
      //   } else {
      //     console.log(response.data);
      //     toast.success("Registered");
      //     handleLogin();
      //   }
      // });
      const data = await axios('http://localhost:5000/auth/register',{
        name,email,password
      })
      console.log(data.status);
  };
  const handleInput = (e, changeState) => {
    changeState(e.target.value);
  };

  return (
    <div className=" h-[100%]  w-[80%] md:w-[70%] rounded-xl flex flex-col  gap-2">
      <h3 className="text-3xl">Create an account</h3>
      {/* <p className="text-orange-500 text-lg">
        Enter valid information!!
      </p> */}
      <form
        className="flex flex-col gap-2 mt-5"
        onSubmit={(e) => handleSubmit(e)}
      >
        <label><strong>Name</strong></label>
        <input
          placeholder="Name"
          className="login-input"
          type="text"
          value={name}
          onChange={(e) => handleInput(e, setName)}
        />
        <label><strong>Email</strong></label>
        <input
          placeholder="Email"
          className="login-input"
          type="email"
          value={email}
          onChange={(e) => handleInput(e, setEmail)}
        />
        <label><strong>Password</strong></label>
        <input
          placeholder="Password"
          className="login-input "
          type="password"
          value={password}
          onChange={(e) => handleInput(e, setPassword)}
        />
        <label><strong>Confirm password</strong></label>
        <input
          placeholder="Confirm password"
          className="login-input"
          value={confirmPassword}
          type="password"
          onChange={(e) => handleInput(e, setConfirmPassword)}
        ></input>

        <button className="login-btn mt-5">Register</button>

        <p className="text-red-500 text-lg m-auto mt-5">
          Have an account?{" "}
          <span>
            {" "}
            <button
              onClick={handleLogin}
              type="submit"
              className="text-md underline text-red-500"
            >
              Login
            </button>
          </span>
        </p>
      </form>
    </div>
  );
};

export default Register;
